{
  "graphs" : [{
      "title": "My metric by host",
      "definition": {
          "events": [],
          "requests": [
              {"q": "sum:my_metric{host:gcp.dev}", "type": "bars"}
          ]
      },
      "viz": "timeseries"
  },{
      "title": "Queries p/second to the MongoDB, with anomaly detection in place",
      "definition": {
          "events": [],
          "requests": [
              {"q": "anomalies(sum:mongodb.opcounters.queryps{database:mongodb}, 'basic', 2)", "type": "line"}
          ]
      },
      "viz": "timeseries"
  },{
      "title": "My metric rolled up and summed by the past hour",
      "definition": {
          "events": [],
          "requests": [
              {"q": "sum:my_metric{host:gcp.dev}.rollup(sum, 3600)", "type": "line"}
          ]
      },
      "viz": "query_value",
      "autoscale": "true"
  }],
  "title" : "Data Visual Timeboard (postman)",
  "description" : "An informative timeboard of the metric 'My Metric'.",
  "read_only": "True"
}
