    "graphs": [
        {
            "definition": {
                "autoscale": false,
                "custom_unit": "\u00b0F",
                "precision": "0",
                "requests": [
                    {
                        "aggregator": "last",
                        "conditional_formats": [],
                        "q": "avg:weather.temp_f_broomfield{*}"
                    }
